mapper.select:
select class_no, class_title, nickname, exercise_name, class_introduce, class_maxcount, class_content, class_price, teacher_content 
from class, member, exercise 
where class.member_no = member.member_no and class.exercise_no = exercise.exercise_no and class_no = #{class_no}

mapper.update:
update class 
set exercise_no = #{exercise_no}, class_title = #{class_title}, class_content = #{class_content}, class_introduce = #{class_introduce}, teacher_content = #{teacher_content}, class_maxcount = #{class_maxcount}, class_price = #{class_price} 
where class_no = #{class_no}
